import LessonSearch from "./search.vue";
import LessonNav from "./nav.vue";
import LessonList from "./list.vue";
import Pagination from 'components/pagination/pagination.vue';
import { getHot } from 'api/common.js';
import { getLessonNav, getLessonList } from 'api/lesson.js';
import { ERR_OK } from 'api/config.js';
export default {
  data: function data() {
    return {
      sort: '',
      params: {},
      page: 1,
      size: 15,
      total: 0,
      lessonList: [],
      navList: [],
      hotList: []
    };
  },
  mounted: function mounted() {
    this.getHotData();
    this.getLessonNavData();
    this.getLessonListData();
  },
  methods: {
    // 分页值更新
    handlePaginationChange: function handlePaginationChange(page) {
      this.page = page;
      this.getLessonListData();
    },
    // 获取热搜词数据
    getHotData: function getHotData() {
      var _this = this;

      getHot().then(function (res) {
        var code = res.code,
            data = res.data,
            msg = res.msg;

        if (code === ERR_OK) {
          _this.hotList = data;
        } else {
          _this.hotList = [];

          _this.$message.error(msg);
        }
      }).catch(function () {
        _this.hotList = [];

        _this.$message.error('接口异常');
      });
    },
    // 获取导航数据
    getLessonNavData: function getLessonNavData() {
      var _this2 = this;

      getLessonNav().then(function (res) {
        var code = res.code,
            data = res.data;

        if (code === ERR_OK) {
          _this2.navList = data;
        } else {
          _this2.navList = [];

          _this2.$message.error('获取课程类型数据失败');
        }
      }).catch(function () {
        _this2.navList = [];

        _this2.$message.error('接口异常');
      });
    },
    // 获取课程列表数据
    getLessonListData: function getLessonListData() {
      var _this3 = this;

      var params = {
        page: this.page,
        size: this.size,
        type: 1,
        category: this.params.category,
        label: this.params.label,
        sort: this.sort
      };
      getLessonList(params).then(function (res) {
        var code = res.code,
            data = res.data,
            msg = res.msg;

        if (code === ERR_OK) {
          _this3.lessonList = data.list;
          _this3.total = data.total;
        } else {
          _this3.lessonList = [];
          _this3.total = 0;

          _this3.$message.error(msg);
        }
      }).catch(function () {
        _this3.lessonList = [];
        _this3.total = 0;

        _this3.$message.error('接口异常');
      });
    }
  },
  watch: {
    params: function params() {
      this.page = 1;
      this.getLessonListData();
    },
    sort: function sort() {
      this.getLessonListData();
    }
  },
  components: {
    LessonSearch: LessonSearch,
    LessonNav: LessonNav,
    LessonList: LessonList,
    Pagination: Pagination
  }
};
